using UnityEngine; using System.Collections; using UnityEngine.UI;   public class spri : MonoBehaviour {  	public GameObject kimono; 	public GameObject tabi; 	public GameObject zori; 	public GameObject dirndl; 	public GameObject hat; 	public GameObject suspenders; 	public GameObject closedWardrobe; 	public GameObject openWardrobe;   	public int country = 0;  	// Update is called once per frame 	void Update () { 		closedWardrobe.SetActive (true); 		if (Input.GetKeyDown("space")){ 			country++; 		}  		if (country == 1) { 			closedWardrobe.SetActive (false); 			openWardrobe.SetActive (true); 			kimono.SetActive (true); 			tabi.SetActive (true); 			zori.SetActive (true); 		}  		if (country == 2) { 			kimono.SetActive (false); 			tabi.SetActive (false); 			zori.SetActive (false); 			dirndl.SetActive (true); 			hat.SetActive (true); 			suspenders.SetActive (true);  		}  else if (country == 3) { 			dirndl.SetActive (false); 			hat.SetActive (false); 			suspenders.SetActive (false); 			closedWardrobe.SetActive (true); 		}   	} }  